from tb_user_info 
where user_id = #{testParam}

select user_id, user_pw, user_nm, user_type
from tb_user_info 
where user_id = #{userId}

mapper.insert:
INSERT INTO tb_user_info
	( user_id
	  , domain_cd
	  , user_pw
	  , user_nm
	)
VALUES 
	( #{userId}
	  , #{domainCd}
	  , #{userPw}
	  , #{userNm}
	)

mapper.update:
	UPDATE tb_user_info
	   SET user_pw      = #{userPw},
	   	   user_nm		= #{userNm}
	 WHERE user_id    	= #{userId}
 	   AND domain_cd    = #{domainCd}

mapper.delete:
	DELETE FROM tb_user_info
     WHERE user_id	= #{userId}
       AND domain_cd    = #{domainCd}
